var rs = require('./rs');

var auth = process.env.AUTH;
if(!auth) process.exit(1);

auth = auth.split(':');
var username = auth[0];
var password = auth[1];

var session = rs.createSession();
session.login(username, password, function() {
    session.mkdir('/a/b/c', function(){
        console.log(arguments);
    });
});
